		list($year, $m, $d) = explode ('-', $date);
		$wd = date( 'N', mktime ( 0, 0, 0, $m, $d, $year ) );
		$easter = $this->bg_get_easter($year);
		
		// Получим рядовые чтения для текущего дня
		$ordinary_refs = array();
		$ordinary_refs[0] = $this->bg_Gospel_at_Matins ($date);
		$ordinary_refs[1] = $this->bg_Apostol_at_Liturgy ($date);
		$ordinary_refs[2] = $this->bg_Gospel_at_Liturgy ($date);
		
		// Рядовые чтения
		$gm = false;
		$text = (!empty($ordinary_refs[2]))?('Ряд: '.($ordinary_refs[0]?(' Утр. - '.$ordinary_refs[0]):'').
				' Лит. - '.$ordinary_refs[1].' '.$ordinary_refs[2].'<br>'):'';

		// На Пасху ни какие другие праздники не служат
		if ($easter != $date ) :

		$before_yesterday = false;
		foreach ($events as $event) {

		// Перенос рядовых чтений из-за праздников
			if ($event["type"] > 2) continue; 		// Только Господские, Богородничные и бденные праздники
			$daysName = ["понедельник", "вторник", "среду", "четверг", "пятницу", "субботу", "воскресенье"];
			if (!$before_yesterday) $before_yesterday = $this->bg_is_event_date ($event, $date, 2);
			$yesterday = $this->bg_is_event_date ($event, $date, 1);
			$tomorow = $this->bg_is_event_date ($event, $date, -1);

			// Обычно рядовые чтения переносим на день перед праздником, при этом воскресные чтения не переносятся ???
			if ($wd < 6 && $tomorow) {	
				$feast = date( 'Y-m-d', mktime ( 0, 0, 0, $m, $d+1, $year ) );
				$feast_wd = date( 'N', mktime ( 0, 0, 0, $m, $d+1, $year ) );
				$text .= 'и за '.$daysName[$feast_wd-1].' (под зачало): '.$this->bg_Apostol_at_Liturgy ($feast).' '.$this->bg_Gospel_at_Liturgy ($feast).'<br>';
			// Eсли случится 2 праздника вподряд, то чтения второго переносим вперед
			} elseif ($wd < 7 && $before_yesterday && $yesterday) {
				$feast = date( 'Y-m-d', mktime ( 0, 0, 0, $m, $d-1, $year ) );
				$feast_wd = date( 'N', mktime ( 0, 0, 0, $m, $d-1, $year ) );
				$text = 'За '.$daysName[$feast_wd-1].': '.$this->bg_Apostol_at_Liturgy ($feast).' '.$this->bg_Gospel_at_Liturgy ($feast).'<br>и за '.$daysName[$feast_wd].' (под зачало) '.$text;
			// А также если праздник случится в понедельник, то чтения переносим вперед, на вторник
			} elseif ($wd == 2 && $yesterday) {
				$feast = date( 'Y-m-d', mktime ( 0, 0, 0, $m, $d-1, $year ) );
				$feast_wd = date( 'N', mktime ( 0, 0, 0, $m, $d-1, $year ) );
				$text = 'За '.$daysName[$feast_wd-1].': '.$this->bg_Apostol_at_Liturgy ($feast).' '.$this->bg_Gospel_at_Liturgy ($feast).'<br>и за '.$daysName[$feast_wd].' (под зачало) '.$text;
			}
		}
		
		// Проверим, есть ли чтения на праздники?
		foreach ($events as $event) {

			if ($this->bg_is_event_date ($event, $date) ) {
				$refs = $event['refs'];
				switch ($event['type']) {
				case 8:		// Субботы и Недели
				case 9:
					if ($gm === false) {	// Нет других событий
						$gm = $refs[0]?$refs[0]:$ordinary_refs[0];
						$text = $event['name'].': '.($gm?(' Утр. - '.$gm):'').' Лит. - '.$refs[1].' '.$refs[2].'<br>';
					} else {
						$text .= $event['name'].':'.' '.$refs[1].' '.$refs[2].'<br>';
					}
					if ($event['type'] == 9) {
						$text .= ' Ряд.(под зачало): '.' '.$ordinary_refs[1].'; '.$ordinary_refs[2].'<br>';
					}
					break;
				case 5:	// Малые и вседневные Минеи
					// В воскресные дни Постной триоди малые и вседневные праздники опускаются
					if ($wd == 7 && $date >= $this->bg_get_easter($year, -70) && $date <= $this->bg_get_easter($year, -7)) break;
				case 4:	// Малые и вседневные Октоиха 	
				case 3:	// Полиелейные
					$text .= $event['name'].':';
					if ($wd < 7 && $event['type'] == 3 && !$gm) {	// Нет других событий
						$gm = $refs[0]?$refs[0]:$ordinary_refs[0];
						$text .= $gm?(' Утр. - '.$gm.' Лит. - '):'';
						$gm = '';
					}
					$text .= ' '.$refs[1].'; '.$refs[2].'<br>';
					break;
				case 2:		// Богородничные и бденные
					$gm = $ordinary_refs[0]?$ordinary_refs[0]:$refs[0];
					$text = '';
					if ($wd == 7 || $wd == 1 || ($date > $this->bg_get_easter($year) && $date < $this->bg_get_easter($year, 50))){
						$text .= ' Ряд.: '.$gm.' Лит. -'.' '.$ordinary_refs[1].' '.$ordinary_refs[2].'<br>';
					}
					$text .= ' '.$event['name'].(($wd==7)?' (под зачало):':($gm?(' Утр. - '.$gm):'')).' Лит. -'.' '.$refs[1].' '.$refs[2].'<br>';
					break;
				case 1:		// Господские
				case 10:	// Господские переходящие
					$gm = $refs[0]?$refs[0]:$ordinary_refs[0];
					$text = $event['name'].': '.($gm?(' Утр. - '.$gm):'').' Лит. - '.' '.$refs[1].' '.$refs[2].'<br>';
					break;
				default:
					
				}
			}
			// Перенос бденных и полиелейных праздников со дня Пасхи на понедельник
			if (($event["type"] == 2 || $event["type"] == 3) &&
				$this->bg_get_easter($year, 1) == $date && $this->bg_is_event_date ($event, $date, 1)) {	
					$refs = $event['refs'];
					$text .= 'и за воскресенье '.$event['name'].': Утр. - '.$refs[0].' Лит. -'.' '.$refs[1].'; '.$refs[2].'<br>';
			}
		}
		
		endif; 
		
		// Дополнительные чтения
		foreach ($additional as $event) {
			if ($this->bg_is_event_date ($event, $date) ) {
				$refs = $event['refs'];
				$text .= $event['name'].': '.($refs[0]?(' Утр. - '.$refs[0]):'').' '.$refs[1].' '.$refs[2].'<br>';
				
			}
		}
		return $text;
	}
